import React, { useState } from 'react';
import SpiceAndWolf from '../../assets/SpiceAndWolf.jpg';
import './home.css'

import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';


export const Home = () => {
  React.useEffect(() => {
    document.addEventListener('keydown', incrementState)
    document.addEventListener("mousedown", incrementState);

    return () => {
      document.removeEventListener('keydown', incrementState)
      document.removeEventListener("mousedown", incrementState);
    }
  }, []) //something about mount and unmount

  const [ welcome, setState ] = useState(true);

  const incrementState = () => {
    setState(false);
    document.removeEventListener('keydown', incrementState);
    document.removeEventListener("mousedown", incrementState);
  }

  return welcome ? init() : main();
}

const main = () => { 
    return (
      <>
        <Carousel>
          <div>
              <img src="assets/1.jpeg" />
              <p className="legend">Legend 1</p>
          </div>
          <div>
              <img src="assets/2.jpeg" />
              <p className="legend">Legend 2</p>
          </div>
          <div>
              <img src="assets/3.jpeg" />
              <p className="legend">Legend 3</p>
          </div>
      </Carousel>
      <ol>
        <li>
          <img src={SpiceAndWolf}/>
          <h1>Server - ToDo</h1>
        </li>
        <li>
          <img src={SpiceAndWolf}/>
          <h1>Skill-Tree</h1>
        </li>
        <li >
          <img src={SpiceAndWolf}/>
          <h1>Gravylang</h1>
        </li>
      </ol>
      </>
    )
  }

const init = () => {
    return (
      <div id='init-div'>
        <div id="init-outer-center">
          <h1 id='init-h1'>Welcome</h1>
          <p id='init-p'>Press any button to continue</p>
        </div>
      </div>
    )
  }

export default Home;
