import React, { useState } from 'react';
import './home.css'

export const Home = () => {
  React.useEffect(() => {
    document.addEventListener('keydown', incrementState)
    document.addEventListener("mousedown", incrementState);

    return () => {
      document.removeEventListener('keydown', incrementState)
      document.removeEventListener("mousedown", incrementState);
    }
  }, []) //something about mount and unmount

  const [ welcome, setState ] = useState(true);

  const incrementState = () => {
    setState(false);
    document.removeEventListener('keydown', incrementState);
    document.removeEventListener("mousedown", incrementState);
  }

  return welcome ? init() : main();
}

const main = () => { 
    return (
      <>
        <div id="height-center">
          <div>
            <ol>
              <li>
                <img src="assets/WeatheringWithYouVertical.jpg" />
                <h1>Server - ToDo</h1>
              </li>
              <li>
                <img src="assets/YourName.jpg" />
                <h1>Skill-Tree</h1>
              </li>
              <li >
                <img src="assets/SpiceAndWolf.jpg" />
                <h1>Gravylang</h1>
              </li>
            </ol>
          </div>
        </div>
        <ol id="bulletContainer">
          <li class="bullet"></li>
          <li class="bullet highlighted"></li>
          <li class="bullet"></li>
        </ol>
      </>
    )
  }

const init = () => {
    return (
      <div id='init-div'>
        <div id="init-outer-center">
          <h1 id='init-h1'>Welcome</h1>
          <p id='init-p'>Press any button to continue</p>
        </div>
      </div>
    )
  }

export default Home;
